	e724:SetType(EFFECT_TYPE_FIELD+EFFECT_TYPE_CONTINUOUS)
	e724:SetCode(EVENT_PREDRAW)
	e724:SetCondition(function(e,tp,eg,ep,ev,re,r,rp)
