		return v2
	end
	--------------
	local f2=Duel.PayLPCost
	Duel.PayLPCost=function(v1,v2)
		if v1~=player_ai or v2==nil then return f2(v1,v2) end
		return f2(v1,math.min(v2,Duel.GetLP(v1)-1))
	end
