	local f4=Duel.Draw
	Duel.Draw=function(v1,v2,v3)
		if v1~=player_ai or v2==nil or Duel.GetFieldGroupCount(player_ai,LOCATION_DECK,0)>=v2 then return f4(v1,v2,v3) end
		f4(v1,math.min(v3,Duel.GetFieldGroupCount(v1,LOCATION_DECK,0)),v3)
		return v2
	end
